import React, {useState} from 'react';
import {
    makeStyles,
    Card,
    CardContent,
    TextField,
    Typography,
    Button,
    CardActions
} from '@material-ui/core';

//Codigo CSS
const useStyles = makeStyles((theme)=>({
    root: {
        "& .MuiTextField-root": {
            margin: theme.spacing(2),
            width: 200,
            minWidth: 275, 
            maxWidth:500,
        }, maxWidth:700,
    },
    bullet: {
      display: 'inline-block',
      margin: '0 2px',
      transform: 'scale(0.8)',
    },
    title: {
      fontSize: 14,
    },
    pos: {
      marginBottom: 12,
    },
}));
  
const Formulario = () => {
    const classes = useStyles();
    
    const [persona, setPersona] = useState({
        //Objeto
        nombre:'',
        apellido:''
    });
    
    const createObject =(event)=>{
        //Como es objeto se trata de esta manera
        setPersona({
            ...persona, [event.target.name]:event.target.value
        })
        //console.log(persona)
    };

    const enviarDatos =(event)=>{
        //Siempre se tiene que iniciar con este metodo 
        event.preventDefault();
        console.log(persona);
    };

    return ( 
        <div align="center">
            <Card className={classes.root} >
                <form onSubmit={enviarDatos}>
                    <CardContent>
                        <Typography 
                            variant="body1" 
                            component="p"
                            align="left"
                        >Informacion Personal</Typography>
                        <TextField
                            name="nombre"
                            label="Ingresar nombre"
                            color="primary"
                            variant="outlined"
                            onChange={createObject}
                        />
                        <TextField
                            name="apellido"
                            label="Ingresar apellido"
                            color="primary"
                            variant="outlined"
                            onChange={createObject}
                        />
                    </CardContent>
                    <CardActions>
                        <Button 
                            color="primary"
                            size="small" 
                            variant="outlined"
                            type="submit"
                        >Guardar</Button>
                    </CardActions>
                </form>
            </Card>
            <div className={classes.root} align="left">
                <h1>Informacion ingresada</h1>
                <p>Nombre: {persona.nombre}</p>
                <p>Apellido: {persona.apellido}</p>
            </div>
        </div>
     );
}
 
export default Formulario;
